mapper.select:
select
from
where 1=1
and ( PRODUCT_NAME LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or PRODUCT_NO LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or PRODUCT_SPEC LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or PRODUCT_FUNC LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or PRODUCT_PERF LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or ADAPTABLE_HARDWARE LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or ADAPTABLE_SOFTWARE LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') or PRODUCT_CERT LIKE CONCAT(CONCAT('%', #{pd.KEYWORDS}),'%') )
and INDUSTRY = #{pd.INDUSTRY}
and PRODUCT_STATE = #{pd.PRODUCT_STATE}
