mapper.select:
SELECT sv_student.`NAME`, sv_student.SNO, sv_student.NUMBER, sv_student.SEX, sv_student.GROUPID, sv_student.SIGN_NO, sv_student.PARENT_NAME, sv_student.PARENT_PHONE, sv_student.KEYPAD_ID, sv_student.REMARK, sv_student.ID FROM sv_student LEFT JOIN sv_classroster cr ON sv_student.ID = cr.STUDENT_ID LEFT JOIN sv_term ON cr.TEAMID = sv_term.TERM_ID WHERE 1 = 1 AND sv_term.START_DATE < NOW() AND sv_term.END_DATE > NOW()
AND cr.SCLASS_ID = #{pd.CLASS_ID}
order by (sv_student.NUMBER ) * 1,(sv_student.KEYPAD_ID * 1),(sv_student.SNO*1)

select sv_student.`NAME`, sv_student.SNO, sv_student.NUMBER, sv_student.SEX, sv_student.GROUPID, sv_student.SIGN_NO, sv_student.PARENT_NAME, sv_student.PARENT_PHONE, sv_student.KEYPAD_ID, sv_student.REMARK, sv_student.ID from sv_student LEFT JOIN sv_classroster cr ON sv_student.ID = cr.STUDENT_ID LEFT JOIN sv_term ON cr.TEAMID = sv_term.TERM_ID WHERE 1 = 1 AND sv_term.START_DATE < NOW() AND sv_term.END_DATE > NOW()
AND cr.SCLASS_ID = #{CLASS_ID}
order by (sv_student.NUMBER ) * 1,(sv_student.KEYPAD_ID * 1),(sv_student.SNO*1)

select sv_student.`NAME`, sv_student.SNO, sv_student.NUMBER, sv_student.SEX, sv_student.GROUPID, sv_student.SIGN_NO, sv_student.PARENT_NAME, sv_student.PARENT_PHONE, sv_student.KEYPAD_ID, sv_student.REMARK, sv_student.ID from sv_student LEFT JOIN sv_classroster cr ON sv_student.ID = cr.STUDENT_ID LEFT JOIN sv_term ON cr.TEAMID = sv_term.TERM_ID WHERE 1 = 1
AND sv_term.TERM_ID = #{pd.TERM_ID}
AND cr.SCLASS_ID = #{pd.CLASS_ID}
order by (sv_student.NUMBER ) * 1,(sv_student.KEYPAD_ID * 1),(sv_student.SNO*1)
